function Script39()
{
  Say("Nee, dat klopt niet. Als Hans een appel eet, dan gaan de koolhydraten naar de maag. Daar veranderen ze in suiker. De suiker komt in het bloed van Hans. Insuline duwt de suiker dan naar Hans zijn spieren. Zo heeft Hans energie om te lopen en te spelen!", { goToNextSlide: true, startWithLaugh: false });
}

function Script40()
